INSERT INTO `x2_questions` VALUES ('393', '2', '禁止党员领导干部讲排场、比阔气、挥霍公款、铺张浪费，不准有以下哪些行为：（）。', '0', '', '', '&lt;p&gt;A、违反规定决定或者批准兴建、装修办公楼、培训中心等楼堂馆所，超标准配备、使用办公用房和办公用品&lt;/p&gt;&lt;p&gt;B、擅自用公款包租、占用客房供个人使用&lt;/p&gt;&lt;p&gt;C、违反规定配备、购买、更换、装饰或者使用小汽车&lt;/p&gt;&lt;p&gt;D、决定或者批准用公款举办有关庆典活动&lt;/p&gt;', '4', 'ABCD', '', 'a:1:{i:0;a:2:{s:7:\"knowsid\";s:1:\"3\";s:5:\"knows\";s:6:\"时政\";}}', '1372247435', '0', '', '0', '0', '1');
