INSERT INTO `x2_questions` VALUES ('417', '2', '下列关于我国经济发展现状的表述，正确的是（）。', '0', '', '', '&lt;p&gt;A、人均国民生产总值已超过3000美元&lt;/p&gt;&lt;p&gt;B、黄金储备量已超过1000吨&lt;/p&gt;&lt;p&gt;C、对石油进口的依存度已接近30%&lt;/p&gt;&lt;p&gt;D、第三产业增加值已接近第二产业&lt;/p&gt;', '4', 'ABD　', '', 'a:0:{}', '1372247435', '0', '', '0', '0', '1');
